  // TypeScript基础示例
  function greet(name: string): string {
    return `Hello, ${name}!`;
  }

  // 使用接口定义对象结构
  interface User {
    id: number;
    name: string;
    email?: string; // 可选属性
  }

  // 创建符合接口的对象
  const user: User = {
    id: 1,
    name: "张三"
  };

  console.log(greet(user.name)); // 输出: Hello, 张三!
